SELECT TabMedia.Title, TabMedia.PublishYear, TabMedia.Budget, TabGenre.GenreName, TabDirector.DirectorName, TabLanguage.LanguageName FROM TabGenre INNER JOIN TabMedia ON TabGenre.GID = TabMedia.Genre INNER JOIN TabDirector ON TabMedia.Director = TabDirector.DID INNER JOIN TabLanguage ON TabMedia.Language = TabLanguage.LID WHERE(TabDirector.DirectorName like IsNull(null, '%')) AND(TabMedia.Title like IsNull(null, '%')) AND(TabGenre.GenreName like IsNull(null, '%'))